<script>
var memberUrl = '<?= ($memberUrl); ?>';




var timer  = null; 
var ajaxer  = null; 



$('.shapeP1Obs').click(function(){
	$(this).siblings('input[type="radio"]').prop('checked', false); 
	var vv = $(this).val();
	var tid = $(this).data('tarto');
	
	$('#'+tid).val(vv);
});



$('.shapeP4obs').change(function(){
	
	var disabledVal = [];
	var currentSelect = $(this);
	
	$(this).parents("ul").find('option').prop( "disabled", false );
	
	$(this).parents("ul").find('select').each(function() {
	
		if($(this).val()){  disabledVal.push($(this).val()); }
		
		
		
	});
	
	//console.log(disabledVal);
	
	
	
	for (i = 0; i < disabledVal.length; i++) {
		
	  $(currentSelect).parents("ul").find("option[value='"+disabledVal[i]+"']").prop( "disabled", true );
	}	
	

	
});





$("#shapeForm").submit(function(event){
    event.preventDefault();
	
	$("#shapeP4").find('option').prop( "disabled", false );
	
	var params = $(this).serialize();
	
	var error='';
	$('.errorTmp').remove();
	$('#shapeForm .fmsg').html('Loading...');
	


	

	$('.shapeP1Val').each(function(index) {
		if($(this).val().trim()==''){
			$(this).before('<span class="errorTmp">This field is required.<br/></span>');
		}
	});
	
	$('.shapeP4obs').each(function(index) {
		if($(this).val().trim()==''){
			$(this).before('<span class="errorTmp">This field is required.<br/></span>');
		}
	});
	
	
	if(!$('.shapeP2obs', this).is(':checked') && $('#shapeP2Other').val().trim()==''){
		$('#shapeP2Ul').before('<span class="errorTmp">This field is required.<br/></span>');
	}
	
		
	
	if(!$('.shapeP3obs', this).is(':checked') && $('#shapeP3Other').val().trim()==''){
		$('#shapeP3Ul').before('<span class="errorTmp">This field is required.<br/></span>');
	}		
	
	if(!$('.shapeP5obs', this).is(':checked')){
		$('#shapeP5Ul').before('<span class="errorTmp">This field is required.<br/></span>');
	}
	
	
	
	

	
	
	
	if (!$('.errorTmp').length) { 
		
		if(timer) clearTimeout(timer); 
		if(ajaxer) ajaxer.abort(); 
		
		
		
		timer = setTimeout(function() {
			
			ajaxer = $.ajax({
				dataType:'html',
				method: "POST",
				url: memberUrl,
				data: params,      
				success:function(data){
					
						$('#shapeForm .fmsg').html(data);
						if(data=='ok'){ location.reload();}
					
				}
			});
			
		 },600);
		 
		
		
	}else{
		$('#shapeForm .fmsg').html('Please fill out all required fields.');
	}
	
});








function setLg(lg) {
  var d = new Date();
  d.setTime(d.getTime() + (30*24*60*60*1000));
  var expires = "expires=" + d.toGMTString();
  var cname = 'userLg';
  document.cookie = cname + "=" + lg + ";" + expires + ";path=/";
  
  location.reload();

}




















