							$html .= '<p class="recommend"><a href="'.base_url().'/class/'.$item['id'].'">'.$item['title'].'</a><br />Date: '.(implode(', ',array_map(function($v){return date("m/d/Y g:i a",$v);},json_decode($item['sessions'])))).'</p>';
							
							
						
						}
						
						// echo date("m/d/Y",$item['start']);
						// echo ' - ';
